targets.expr:
sum by (datname, rolname, query) (
  increase(pg_stat_statements_total_time{instance="$instance", datname="$datname"}[$__range])
) / (ceil(
  sum by (datname, rolname, query) (
    increase(pg_stat_statements_calls_total{instance="$instance", datname="$datname"}[$__range]) > 0
  )))
